pub enum FixedGradient {

    /// A linear gradient.
    Linear(FixedLinearGradient),

    /// A radial gradient.
    Radial(FixedRadialGradient),
}

pub enum ScaleMode {

    /// The unit 1.0 is mapped to the smaller of width & height, but the mapped
    /// item may not cover the entire rectangle.
    Fit,

    /// The unit 1.0 is mapped to the larger of width & height; some
    /// values on the other axis may extend outside the target rectangle.
    Fill,
}

pub enum InterpolationMode {

    /// Don't interpolate, use nearest neighbor.
    NearestNeighbor,

    /// Use bilinear interpolation.
    Bilinear,
}

pub enum ImageFormat {

    /// 3 bytes per pixel, in RGB order.
    Rgb,

    /// 4 bytes per pixel, in RGBA order, with separate alpha.
    RgbaSeparate,

    /// 4 bytes per pixel, in RGBA order, with premultiplied alpha.
    RgbaPremul,

    /// More formats may be added later.
    #[doc(hidden)]
    _NonExhaustive,
}

pub enum PaintBrush {
    Color(Color),
    Linear(LinearGradient),
    Radial(RadialGradient),
    Fixed(FixedGradient),
}
